$(document).ready(function(){var b=175;var a=475;var c=0,d=5;$(this).scrollTop(0);$(window).on("scroll load resize",function(){var e=$(this).scrollTop();if(e>b){$("#transmenu").removeClass("transparency")}else{$("#transmenu").addClass("transparency")}if(Math.abs(c-e)<=d){return}if(e>a&&e>c){$("#transmenu").fadeOut(600)}else{$("#transmenu").fadeIn(600)}c=e});$("#transmenu .dropdown").on("show.bs.dropdown",function(){$(this).find(".dropdown-menu").first().stop(true,true).slideDown(300)});$("#transmenu .dropdown").on("hide.bs.dropdown",function(){$(this).find(".dropdown-menu").first().stop(true,true).slideUp(300)})});